import React, { useState } from 'react';

const App = () => {
    let purplea = 'purple';
    const [bg, newbg] = useState(purple);

    const bgchange = () =>{
        let yellow = 'yellow';
        newbg(yellow);
    }
    
    return(
        <>
        
        <button onClick={bgchange} style={{ backgroundColor: purplea , color: 'white', fontSize:'20px'}}>heheheheheheheheh</button>

        </>
    )
}

export default App;
