import React, { useState } from 'react';

const App = () => {
    const[num, setNum] = useState();
    const[num2, setNum2] = useState();
    const[num3, setNum3] = useState();
    const[click, setClick] = useState();
    const[click2, setClick2] = useState();


    
    var crt = Math.cbrt(num);
    var srt = Math.sqrt(num);
    const sqrt = () => {
        setNum2(num);
       setClick(Math.sqrt(num));
    }

    const cbrt = () => {
        setNum3(num);
       setClick2(Math.cbrt(num));
    }

    const value = (event) => {
        setNum(event.target.value);
    }

return(
    <>
    <div>
     <h1>your square root of ___{num2} is {click}</h1>
     <h1>your Cube root of ____{num3} is {click2}</h1>
    <input type="text" id="text" placeholder="type a number" value={num} onChange={value} style={{marginLeft: '10px', marginTop: '5px'}}/><br/>
    <button onClick={sqrt} style={{marginLeft: '10px', marginTop: '5px'}}>Square Root</button>
    <button onClick={cbrt} style={{marginLeft: '10px', marginTop: '5px'}}>Cube Root</button>
    </div>
    </>
)

}

export default App;
