import React, { useState } from 'react';

const App = () => {
    let purple = '#eee';
    let text1 ='hehehehe';
    const [bg, setbg] = useState(purple);
    const [name, newname] = useState(text1);

    const bgchange = () => {
        let Black = 'Black';
        let text2 = 'mocambu khush hua';
        newname(text2);
        setbg(Black);
    }
    
    return(
        <>
        
        <button onClick={bgchange} style={{ backgroundColor: {purple} , color: 'white', fontSize:'20px'}}>{name}</button>

        </>
    )
}

export default App;
