    var srt = Math.sqrt(num);
    const sqrt = () => {
        setNum2(Math.sqrt(num));
       setClick();
    }

    const cbrt = () => {
        setNum3(num);
       setClick2(Math.cbrt(num));
    }

    const value = (event) => {
        setNum(event.target.value);
