import React, { useState } from 'react';

const App = () => {
    let purple = 'purple';
    let he ='hehehehe';
    const [bg, setbg] = useState(purple);
    const [name, newname] = useState();

    const bgchange = () => {
        let Black = 'Black';
        setbg(Black);
    }
    
    return(
        <>
        
        <button onClick={bgchange} style={{ backgroundColor: bg , color: 'white', fontSize:'20px'}}>{name}</button>

        </>
    )
}

export default App;
